var auroraDb = Amazonaurora.AmazonAuroraVectorStore.FromExistingAuroraVectorStore(this, "ExistingAuroraVectorStore", new ExistingAmazonAuroraVectorStoreProps {
    ClusterIdentifier = "aurora-serverless-vector-cluster",
    DatabaseName = "bedrock_vector_db",
    SchemaName = "bedrock_integration",
    TableName = "bedrock_kb",
    VectorField = "embedding",
    TextField = "chunks",
    MetadataField = "metadata",
    PrimaryKeyField = "id",
    EmbeddingsModelVectorDimension = Bedrock.BedrockFoundationModel.COHERE_EMBED_ENGLISH_V3.VectorDimensions,
    Vpc = Aws_ec2.Vpc.FromLookup(this, "VPC", new VpcLookupOptions {
        VpcId = "vpc-0c1a234567ee8bc90"
    }),
    AuroraSecurityGroup = Aws_ec2.SecurityGroup.FromSecurityGroupId(this, "AuroraSecurityGroup", "sg-012456789"),
    Secret = Aws_rds.DatabaseSecret.FromSecretCompleteArn(this, "Secret", Stack.Of(this).FormatArn(new ArnComponents {
        Service = "secretsmanager",
        Resource = "secret",
        ResourceName = "rds-db-credentials/cluster-1234567890",
        Region = Stack.Of(this).Region,
        Account = Stack.Of(this).Account,
        ArnFormat = ArnFormat.COLON_RESOURCE_NAME
    }))
});

var kb = new Bedrock.VectorKnowledgeBase(this, "KnowledgeBase", new VectorKnowledgeBaseProps {
    EmbeddingsModel = Bedrock.BedrockFoundationModel.COHERE_EMBED_ENGLISH_V3,
    VectorStore = auroraDb,
    Instruction = "Use this knowledge base to answer questions about books. " + "It contains the full text of novels."
});

var docBucket = new Aws_s3.Bucket(this, "DocBucket");

new Bedrock.S3DataSource(this, "DataSource", new S3DataSourceProps {
    Bucket = docBucket,
    KnowledgeBase = kb,
    DataSourceName = "books",
    ChunkingStrategy = Bedrock.ChunkingStrategy.FixedSize(new FixedSizeChunkingConfigurationProperty {
        MaxTokens = 500,
        OverlapPercentage = 20
    })
});

