var batchBucket = new Bucket(this, "BedrockBatchBucket");

var batchPolicy = new ManagedPolicy(this, "BatchPolicy", new ManagedPolicyProps { });

batchPolicy.AddStatements(
new PolicyStatement(new PolicyStatementProps {
    Sid = "Inference",
    Actions = new [] { "bedrock:InvokeModel", "bedrock:CreateModelInvocationJob" },
    Resources = new [] { $"arn:aws:bedrock:{this.region}::foundation-model/*" }
}));

var bedrockBatchSfnFragment = new Bedrock_batch_stepfn.BedrockBatchSfn(this, "AwsBedrockBatchSfn", new BedrockBatchSfnProps {
    BedrockBatchInputBucket = batchBucket,
    BedrockBatchOutputBucket = batchBucket,
    BedrockBatchPolicy = batchPolicy,
    Timeout = Duration.Hours(48)
});

var inputState = new Pass(this, "InputState", new PassProps {
    Parameters = new Dictionary<string, object> {
        { "job_name", "test_job" },
        { "manifest_keys", new [] { "test_key.jsonl" } },
        { "model_id", "test.model-v1" }
    }
});

var outputState = new Pass(this, "OutputState");

var failState = new Fail(this, "FailState", new FailProps {
    CausePath = JsonPath.StringAt("$.cause"),
    ErrorPath = JsonPath.StringAt("$.error")
});

var chain = inputState.Next(bedrockBatchSfnFragment).Next(outputState);

for (var endState in bedrockBatchSfnFragment.EndStates)
{
    if (endState instanceof TaskStateBase)
    {
        endState.AddCatch(failState);
    }
}

var stateMachine = new StateMachine(this, "StateMachine", new StateMachineProps {
    DefinitionBody = DefinitionBody.FromChainable(chain)
});

java.source:
Bucket batchBucket = new Bucket(this, "BedrockBatchBucket");

ManagedPolicy batchPolicy = ManagedPolicy.Builder.create(this, "BatchPolicy").build();

batchPolicy.addStatements(
PolicyStatement.Builder.create()
        .sid("Inference")
        .actions(List.of("bedrock:InvokeModel", "bedrock:CreateModelInvocationJob"))
        .resources(List.of(String.format("arn:aws:bedrock:%s::foundation-model/*", this.region)))
        .build());

BedrockBatchSfn bedrockBatchSfnFragment = BedrockBatchSfn.Builder.create(this, "AwsBedrockBatchSfn")
        .bedrockBatchInputBucket(batchBucket)
        .bedrockBatchOutputBucket(batchBucket)
        .bedrockBatchPolicy(batchPolicy)
        .timeout(Duration.hours(48))
        .build();

Pass inputState = Pass.Builder.create(this, "InputState")
        .parameters(Map.of(
                "job_name", "test_job",
                "manifest_keys", List.of("test_key.jsonl"),
                "model_id", "test.model-v1"))
        .build();

Pass outputState = new Pass(this, "OutputState");

Fail failState = Fail.Builder.create(this, "FailState")
        .causePath(JsonPath.stringAt("$.cause"))
        .errorPath(JsonPath.stringAt("$.error"))
        .build();

Chain chain = inputState.next(bedrockBatchSfnFragment).next(outputState);

for (Object endState : bedrockBatchSfnFragment.getEndStates()) {
    if (endState instanceof TaskStateBase) {
        endState.addCatch(failState);
    }
}

StateMachine stateMachine = StateMachine.Builder.create(this, "StateMachine")
        .definitionBody(DefinitionBody.fromChainable(chain))
        .build();

go.source:
batchBucket := s3.NewBucket(this, jsii.String("BedrockBatchBucket"))

batchPolicy := iam.NewManagedPolicy(this, jsii.String("BatchPolicy"), &ManagedPolicyProps{
})

batchPolicy.AddStatements(
iam.NewPolicyStatement(&PolicyStatementProps{
	Sid: jsii.String("Inference"),
	Actions: []*string{
		jsii.String("bedrock:InvokeModel"),
		jsii.String("bedrock:CreateModelInvocationJob"),
	},
	Resources: []*string{
		fmt.Sprintf("arn:aws:bedrock:%v::foundation-model/*", this.Region),
	},
}))

bedrockBatchSfnFragment := bedrock_batch_stepfn.NewBedrockBatchSfn(this, jsii.String("AwsBedrockBatchSfn"), &BedrockBatchSfnProps{
	BedrockBatchInputBucket: batchBucket,
	BedrockBatchOutputBucket: batchBucket,
	BedrockBatchPolicy: batchPolicy,
